export = ApiError;
declare class ApiError extends Error {
    constructor(...args: any[]);
    _hueError: import("./HueError");
    getHueError(): import("./HueError");
    /**
     * @returns {number}
     */
    getHueErrorType(): number;
    /**
     * @returns {string | null}
     */
    getHueErrorAddress(): string;
    /**
     * @returns {string | null}
     */
    getHueErrorDescription(): string;
    /**
     * @returns {string | null}
     */
    getHueErrorMessage(): string;
}
